mapper.select:
SELECT COUNT(*)
FROM GROUP_MEMBER
WHERE G_NO=#{gNo}
AND GM_ID=#{gmId}
AND GM_STATUS = 'Y'

mapper.update:
UPDATE GROUP_MEMBER
SET
	GM_STATUS='N'
WHERE
	G_NO=#{ gNo }
AND GM_ID=#{ gmId }

mapper.select:
SELECT * FROM GROUP_MEMBER
WHERE G_NO=#{ gNo }
AND GM_STATUS='Y'

SELECT * FROM GROUP_MEMBER
WHERE G_NO=#{ gNo }
AND GM_STATUS='N'
