mapper.select:
SELECT COUNT(*)
FROM GROUP_MEMBER
WHERE G_NO=#{gNo}
AND GM_ID=#{gmId}
AND GM_STATUS = 'Y'

SELECT COUNT(*)
FROM GROUP_MEMBER
WHERE G_NO=#{gNo}
AND GM_ID=#{gmId}

mapper.delete:
DELETE GROUP_MEMBER
WHERE
	G_NO=#{ gNo }
AND GM_ID=#{ gmId }

mapper.update:
UPDATE GROUP_MEMBER
SET
	GM_STATUS='D'
WHERE
	G_NO=#{ gNo }
AND GM_ID=#{ gmId }

mapper.select:
